import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  const changeHandlerTitle = (e) => {
    setTitle(e.target.value);
    //console.log(title);

  }

  const changeHandlerDate = (e) => {
    setDate(e.target.value);
   // console.log(date);
  }

  const changeHandlerTime = (e) => {
    setTime(e.target.value);
    //console.log(time);
  }

  const changeHandlerContact = (e) => {
    setContact(e.target.value)
  }

  return (
    
      <form onSubmit={handleSubmit}>

        <label htmlFor="title">Title:</label>
        <input type="text"id="title" name="title" onChange={changeHandlerTitle} value={title}/>

        <label htmlFor="date">Date:</label>
        <input type="date" id="date" name="date" onChange={changeHandlerDate} value={date}/>

        <label htmlFor="time">Time:</label>
        <input type="time" id="time" name="time" value={time} onChange={(e)=> changeHandlerTime(e)}  />

        <select>
          {contacts.map((person, index) => {
            return <option key={index} value={contact}>{person.name}</option>
          })}
        </select>

        <button type="submit">Add Appointment</button>

      </form>
    
  );
};
